import React from "react";
import styles from "./Navbar.module.css";
import Button from "@material-ui/core/Button";
import { makeStyles } from "@material-ui/core/styles";
import Modal from "@material-ui/core/Modal";
import Backdrop from "@material-ui/core/Backdrop";
import Fade from "@material-ui/core/Fade";
import { TextField } from "@material-ui/core";
import { useDispatch, useSelector } from "react-redux";
import { useHistory } from "react-router-dom";
import { authUser, logoutUser, createUser } from "../../Redux/Auth/action";
const useStyles = makeStyles((theme) => ({
  modal: {
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },
  paper: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    width: 350,
    textAlign: "center",
    borderRadius: 10,
    padding: theme.spacing(2, 4, 3),
  },
  paperSignUp: {
    backgroundColor: theme.palette.background.paper,
    boxShadow: theme.shadows[5],
    width: 350,
    textAlign: "center",
    borderRadius: 10,
    padding: theme.spacing(2, 4, 3),
  },
  inputs: {
    width: "100%",
    marginTop: 50,
  },
  textField: {
    width: "100%",
    marginTop: 15,
  },
  LoginBtn: {
    marginTop: 50,
    color: "white",
    width: "40%",
    backgroundColor: "#0EBABA",
    "&:hover": {
      color: "black",
    },
  },
}));
function Navbar() {
  const [email, setEmail] = React.useState("");
  const [name, setUsername] = React.useState("");
  const [password, setPassword] = React.useState("");
  let history = useHistory();
  const classes = useStyles();
  const dispatch = useDispatch();
  const [openLogin, setOpenLogin] = React.useState(false);
  const [openSignUp, setOpenSignUp] = React.useState(false);
  const isAuth = useSelector((state) => state.auth.isAuth);
  const currentUser = useSelector((state) => state.auth.currentUser);
  const token = useSelector((state) => state.auth.token);
  // console.log(isAuth);
  // console.log(currentUser.name);
  // console.log(token);

  const handleLogin = () => {
    const payload = {
      email,
      password,
    };

    dispatch(authUser(payload));
  };

  const guestClick = () => {
    history.push("/BookList");
  };

  const handleCreateUser = () => {
    const payload = {
      name,
      email,
      password,
    };
    dispatch(createUser(payload));
  };
  const handleOpenLogin = () => {
    setOpenLogin(true);
  };

  const handleCloseLogin = () => {
    setOpenLogin(false);
  };
  const handleOpenSignUp = () => {
    setOpenSignUp(true);
  };

  const handleCloseSignUp = () => {
    setOpenSignUp(false);
  };
  const [toggleLogin, setToggleLogin] = React.useState(false);
  const [toggleUser, setToggleUser] = React.useState(false);

  React.useEffect(() => {
    if (isAuth) {
      setToggleLogin(true);
    } else {
      setToggleLogin(false);
    }
  }, [isAuth]);
  return (
    <div className={styles.Navbar}>
      <div className={styles.Navbar__Logo}>
        <div style={{ cursor: "pointer" }} onClick={() => history.push("/")}>
          <img className={styles.img} src="https://toppng.com/uploads/preview/experience-the-discussion-online-library-book-logo-11562996058ru4peza6zq.png" alt="Logo" />
        </div>
      </div>
      <div></div>

      <div className={styles.Navbar__Button__Info}>
        <Button onClick={guestClick}>Available Books</Button>
        {! isAuth && <Button className={styles.loginBtn} onClick={handleOpenLogin}>Login</Button>}

        {!toggleLogin ? (
          <>

            <Modal
              aria-labelledby="transition-modal-title"
              aria-describedby="transition-modal-description"
              className={classes.modal}
              open={openLogin}
              onClose={handleCloseLogin}
              closeAfterTransition
              BackdropComponent={Backdrop}
              BackdropProps={{
                timeout: 500,
              }}
            >
              <Fade in={openLogin}>
                <div className={classes.paper}>
                  <h4 id="transition-modal-title">Login</h4>
                  <div className={classes.inputs}>
                    <TextField
                      className={classes.textField}
                      id="standard-basic"
                      label="Enter Email"
                      onChange={(e) => setEmail(e.target.value)}
                    />
                    <TextField
                      className={classes.textField}
                      id="standard-basic"
                      label="Enter Password"
                      type="password"
                      onChange={(e) => setPassword(e.target.value)}
                    />
                  </div>

                  <Button className={classes.LoginBtn} onClick={handleLogin}>
                    Login
                  </Button>

                  <div className={styles.SignUp}>
                    <p>Don't have a account yet?</p>
                    <button
                      onClick={() => {
                        handleOpenSignUp();
                        handleCloseLogin();
                      }}
                    >
                      Sign Up
                    </button>
                  </div>
                  <hr />

                  <div className={styles.SocialLogin}>
                    <Button style={{ position: "relative" }}>
                      {" "}
                      <img
                        src="data:image/svg+xml;base64,"
                        alt="google"
                        style={{
                          width: "20px",
                          position: "absolute",
                          left: "10px",
                        }}
                      />
                      Google{" "}
                    </Button>
                    <Button style={{ position: "relative" }}>
                      <img
                        src="data:image/svg+xml;base64,"
                        alt="facebook"
                        style={{
                          width: "20px",
                          position: "absolute",
                          left: "10px",
                        }}
                      />
                      Facebook{" "}
                    </Button>
                  </div>
                </div>
              </Fade>
            </Modal>

            <Modal
              aria-labelledby="transition-modal-title"
              aria-describedby="transition-modal-description"
              className={classes.modal}
              open={openSignUp}
              onClose={handleCloseSignUp}
              closeAfterTransition
              BackdropComponent={Backdrop}
              BackdropProps={{
                timeout: 500,
              }}
            >
              <Fade in={openSignUp}>
                <div className={classes.paperSignUp}>
                  <h4 id="transition-modal-title">Sign Up For Artist User</h4>
                  <div className={classes.inputs}>
                    <TextField
                      className={classes.textField}
                      id="standard-basic"
                      label="Enter Username"
                      onChange={(e) => setUsername(e.target.value)}
                    />
                    <TextField
                      className={classes.textField}
                      id="standard-basic"
                      label="Enter Email"
                      onChange={(e) => setEmail(e.target.value)}
                    />
                    <TextField
                      className={classes.textField}
                      id="standard-basic"
                      label="Enter Password"
                      onChange={(e) => setPassword(e.target.value)}
                    />
                  </div>

                  <Button
                    className={classes.LoginBtn}
                    onClick={handleCreateUser}
                  >
                    SignUp
                  </Button>
                </div>
              </Fade>
            </Modal>
          </>
        ) : (
          <>
            <Button onMouseOver={() => setToggleUser(!toggleUser)}>
              {currentUser.name}
            </Button>
            {toggleUser && (
              <div
                className={styles.Login__DropDown}
                onMouseLeave={() => setToggleUser(!toggleUser)}
              >
                <Button
                  style={{ width: "80%" }}
                  onClick={() => history.push("/profile")}
                >
                  Profile
                </Button>

                <Button
                  style={{ width: "80%" }}
                  onClick={() => {
                    dispatch(logoutUser());
                  }}
                >
                  Logout
                </Button>
              </div>
            )}
          </>
        )}
      </div>
    </div>
  );
}

export default Navbar;
